mapper.select:
SELECT cust.customer_turename, cust.customer_phone, dep.dep_name, cust.customer_current_salary, cust.customer_current_expense, t1.expense_sum, pr.real_wage, cust.customer_id, cust.customer_is_expense FROM customers cust LEFT JOIN customers_station cs ON cust.customer_id = cs.station_customer_id LEFT JOIN company_deps dep ON dep.dep_id = cs.station_dept_id LEFT join customer_payroll pr on pr.customer_id = cust.customer_id LEFT JOIN ( SELECT exp.customer_id, sum(exp.expense_amount) as expense_sum FROM customer_expense_detail exp WHERE exp.cycle_id = ${dto.payCycleId} and exp.company_id = ${dto.companyId} GROUP BY exp.cycle_id, exp.customer_id ) t1 ON t1.customer_id = cust.customer_id WHERE (pr.pay_cycle_id = ${dto.payCycleId}) AND cust.customer_company_id = ${dto.companyId} AND cs.station_customer_state < 4 AND ((cs.station_dimissing_time is null) or (cs.station_dimissing_time is not null and cs.station_dimissing_time >= #{startDate}))
AND cs.station_employ_method = ${dto.stationEmployMethod}
AND cs.station_customer_state = ${dto.stationCustomerState}
AND cs.station_dept_id = ${dto.deptId}
AND cust.customer_turename LIKE concat('%','${dto.userName}','%')
AND cust.customer_is_expense = ${dto.isExpense}
