#{redCount,jdbcType=INTEGER}, #{redMoney,jdbcType=DECIMAL}, #{redScope,jdbcType=INTEGER},
#{sourceType,jdbcType=INTEGER}, #{addTime,jdbcType=TIMESTAMP}, #{description,jdbcType=VARCHAR},
#{useStartTime,jdbcType=TIMESTAMP}, #{useEndTime,jdbcType=TIMESTAMP}, #{state,jdbcType=INTEGER}
)
