mapper.select:
SELECT DISTINCT

FROM sys_menu m
WHERE is_delete = 0 AND parent_id IS NOT NULL
AND parent_id = #{parentId,jdbcType=BIGINT}

    AND EXISTS (
    SELECT * FROM sys_role_rel r
    WHERE r.obj_id = m.id AND r.rel_type = 0
    AND EXISTS (
    SELECT 1 FROM sys_role_rel u WHERE u.role_id = r.role_id AND u.rel_type = 1 AND u.obj_id =
    #{userId,jdbcType=BIGINT}
    )
    )

ORDER BY sort
